import { Disclosure, RadioGroup, Tab } from "@headlessui/react";
import { StarIcon } from "@heroicons/react/20/solid";
import { HeartIcon, MinusIcon, PlusIcon } from "@heroicons/react/24/outline";
import { useState, useEffect } from "react";
import Skeleton from "react-loading-skeleton";
import { useParams } from "react-router-dom";

function classNames(...classes) {
  return classes.filter(Boolean).join(" ");
}

function WithImageGalleryAndExpandableDetails() {
  const [product, setProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState(null);
  const { id: productId } = useParams();

  useEffect(() => {
    // Define the API URL with the productId
    const apiUrl = `http://localhost:8000/api/product/${productId}/`;

    // Fetch the data from the API
    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setProduct(data);
        setSelectedColor(data.characteristics); // Set the default selected color
      })
      .catch((error) => {
        console.error("Error fetching product:", error);
      });
  }, [productId]);


  return (
    <div className="bg-white">
      <div className="mx-auto max-w-2xl px-0 py-8 lg:max-w-7xl">

        {product && selectedColor && selectedColor[0] != null && selectedColor[0].images != null ?(

        <div className="mx-auto mt-6 w-full overflow-x-auto max-w-2xl sm:block lg:max-w-none">
  <Tab.List className="flex gap-6">
    {selectedColor[0].images.length !== 0 ? (
      selectedColor.map((characteristics) =>
        characteristics.images.map((img) => (
          <Tab
            key={img.middle}
            className="relative flex h-24 cursor-pointer items-center justify-center rounded-md bg-white text-sm font-medium uppercase text-gray-900 hover-bg-gray-50 focus:outline-none focus:ring focus:ring-opacity-50 focus:ring-offset-4"
          >
            {({ selected }) => (
              <>
                <span className="sr-only">{selectedColor[0].value}</span>
                <span className="absolute inset-0 overflow-hidden rounded-md">
                  <img
                    src={img.middle}
                    alt=""
                    className="h-full w-full object-cover object-center"
                  />
                </span>
                <span
                  className={classNames(
                    selected ? "ring-indigo-500" : "ring-transparent",
                    "pointer-events-none absolute inset-0 rounded-md ring-2 ring-offset-2"
                  )}
                  aria-hidden="true"
                />
              </>
            )}
          </Tab>
        ))
      )
    ) : (
      <>
        {[1, 2, 3, 4].map((img) => (
          <Tab
            key={img}
            className="relative flex h-24 cursor-pointer items-center justify-center rounded-md bg-white text-sm font-medium uppercase text-gray-900 hover-bg-gray-50 focus:outline-none focus:ring focus:ring-opacity-50 focus:ring-offset-4"
          >
            <span className="absolute inset-0 overflow-hidden rounded-md">
              <div
                className="animate-pulse bg-gray-200 h-full w-full object-cover object-center"
              />
            </span>
            <span
              className={classNames(
                "ring-transparent",
                "pointer-events-none absolute inset-0 rounded-md ring-2 ring-offset-2"
              )}
              aria-hidden="true"
            />
          </Tab>
        ))}
      </>
    )}
  </Tab.List>
</div>

        ):
        (
          <div className="lg:grid lg:grid-cols-2  lg:items-start lg:gap-x-8">
          <Tab.Group as="div" className="flex flex-col-reverse">
            {/* Image selector */}
            <div className="mx-auto mt-6 hidden w-full max-w-2xl sm:block lg:max-w-none">
              <Tab.List className="grid grid-cols-4 gap-6">
                {[1, 2].map((characteristics) =>
                  [1, 2].map((img) => (
                    <Tab
                      className="relative flex h-24 cursor-pointer items-center justify-center rounded-md bg-white text-sm font-medium uppercase text-gray-900 hover-bg-gray-50 focus:outline-none focus:ring focus:ring-opacity-50 focus:ring-offset-4"
                    >
                        <span className="absolute inset-0 overflow-hidden rounded-md">
                          <div
                            className="animate-pulse bg-gray-200 h-full w-full object-cover object-center"
                          />
                        </span>
                        <span
                          className={classNames(
                            "ring-transparent",
                            "pointer-events-none absolute inset-0 rounded-md ring-2 ring-offset-2"
                          )}
                          aria-hidden="true"
                        />
                    </Tab>
                  ))
                )}
              </Tab.List>
            </div>

            <div
              className="animate-pulse size-full h-96 y-2 bg-gray-200 sm:rounded-lg"
            ></div>
          </Tab.Group>

          
        </div>
        )}

      </div>
    </div>
  );
}

export default WithImageGalleryAndExpandableDetails;
